package graph;

/*
 *  * See {@link https://en.wikipedia.org/wiki/Circle} for the mathematical definition.

 */
/**
 * 
 * Graph representation of a Circle.
 * 
 *<br>
 *See Wikipedia article on  <a href=https://en.wikipedia.org/wiki/Circle>Circle</a>
 * <br>
 * <img alt="Circle" src=" data:image/png;base64,">
 * @author rsant
 * @since 1.0
 * 
 */
public class Circle implements Gobject{

	/**
	 *
	 * center point abscissa
	 */

	private double xCenter;
	/**
	 * center point ordinate
	 */
	private double yCenter;
	/**
	 * Radius.
	 */
	private double radius;
	public Circle(double x,double y,double r) {
		xCenter=x;
		yCenter=y;
		radius= r;
				
	}
	/**
	 * {@inheritDoc}
	 * The distance to the center is sqrt( (x-xCenter)^2 +(y-yCenter)^2).
	 * <p>
	 * If the distance is greater that radius the the point is outside the circle, if it is equal is in 
	 * the border and if it shorter then is inside.
	 * <P>
	 * Since we only care about the sign, we return the difference of the squares quantities .
	 * 
	 * 
	 */
	
	public int belongs(double x, double y) {
		double x2=Math.pow(x-xCenter,2);
		double y2=Math.pow(y-yCenter, 2);
		double r2=radius*radius;
		double dist= x2+y2-r2;
		return  dist==0?Gobject.BORDER:(dist<0?Gobject.INSIDE:Gobject.OUTSIDE);
	}
	/**
	 * Cannot represent a circle connecting dots.
	 * 
	 * {@inheritDoc}
	 * 
	 * @throws GraphException always 
	 */
	public double[][] border() throws GraphException {
		throw new GraphException("Cant create border for Circle");
	}

	
}
